import React from "react";
import Container from "./container";
import "./crypto.css";
import { Link } from "react-router-dom";

class CryptoSearch extends React.Component {
  render(props) {
    const { searchResults } = this.props;
    const link = `https://www.cryptocompare.com${searchResults.ImageUrl}`;
    return (
      <Link
        style={{ textDecoration: "none" }}
        to={{
          pathname: `/searched/${searchResults.Id}`,
          data: searchResults
        }}
      >
        <div className="flexm">
          <Container>
            <img
              className="coin-image"
              src={link}
              alt={searchResults.link}
              width="70px"
            />
            <h3 className="card-title">{searchResults.CoinName}</h3>
          </Container>
        </div>
      </Link>
    );
  }
}

export default CryptoSearch;
